/*Table structure for table `auth_role` */

DROP TABLE IF EXISTS `auth_role`;

CREATE TABLE `auth_role` (
  `ID` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '角色ID',
  `CODE` varchar(30) NOT NULL COMMENT '角色编码',
  `NAME` varchar(30) DEFAULT NULL COMMENT '角色名称',
  `STATUS` tinyint(4) NOT NULL DEFAULT '1' COMMENT '状态   1:正常、0：禁用',
  `CREATE_TIME` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `UPDATE_TIME` datetime DEFAULT NULL COMMENT '更新时间',
  `create_by` varchar(30) DEFAULT NULL COMMENT '创建者',
  `update_by` varchar(30) DEFAULT NULL COMMENT '更新者',
  `remark` varchar(255) DEFAULT NULL COMMENT '备注',
  `domain_id` bigint(20) NOT NULL DEFAULT '-1' COMMENT '子站编号',
  PRIMARY KEY (`ID`),
  UNIQUE KEY `auth_role_pk` (`CODE`,`domain_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=214 DEFAULT CHARSET=utf8mb4 COMMENT='角色信息表';

