/*Table structure for table `auth_user` */

DROP TABLE IF EXISTS `auth_user`;

CREATE TABLE `auth_user` (
  `uid` varchar(30) NOT NULL COMMENT 'uid,用户账号,主键',
  `domain_id` bigint(20) NOT NULL DEFAULT '-1' COMMENT '域ID，一个后台用户只属于一个域',
  `username` varchar(30) DEFAULT NULL COMMENT '用户名(nick_name)',
  `password` varchar(50) DEFAULT NULL COMMENT '密码(MD5(密码+盐))',
  `salt` varchar(20) DEFAULT NULL COMMENT '盐',
  `real_name` varchar(30) DEFAULT NULL COMMENT '用户真名',
  `avatar` varchar(100) DEFAULT NULL COMMENT '头像',
  `status` tinyint(4) DEFAULT '1' COMMENT '账户状态(1.正常 2.锁定 3.删除 4.非法)',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime DEFAULT NULL COMMENT '更新时间',
  `create_by` varchar(30) DEFAULT NULL COMMENT '创建者',
  `update_by` varchar(30) DEFAULT NULL COMMENT '更新者',
  `remark` varchar(255) DEFAULT NULL COMMENT '备注',
  `parent_user_id` varchar(30) DEFAULT NULL COMMENT '用户上级',
  `secret_key` varchar(20) DEFAULT NULL COMMENT '谷歌验证码密匙',
  `secret_qrcode` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`uid`),
  UNIQUE KEY `auth_user_pk` (`uid`,`domain_id`) USING BTREE
